import React from "react"
import reactDom from "react-dom"
import AppTravel from "../table/appTableTravel"
import TravelComplete from "../travel/travelComplete"

class TravelList extends React.Component {
    constructor() {
        super()
        this.state = {
            title: "",
            todolist:[{title:'Trivandrum',status:true},{title:'Bangalore City',status:false},
            {title:'Chennai',status:false},{title:'Kochin',status:false}]
            
            
        }
        
    }

    updatePending=(title)=>{
        let todolist = this.state.todolist;
       let updatedtodo=todolist.map(item=>{
            if (item.title===title)
            {
                item.status=true;
            }
            return item
        })
        this.setState(({ todolist: updatedtodo }))
    }
    
    onDelete=(index)=>{
        let updatedtodo = this.state.todolist;
        updatedtodo.splice(index,1)

        this.setState(({ todolist: updatedtodo }))
    }
    setData = (event) => {
        this.setState({ title: event.target.value})
    }
    saveData = () => {
        const todolist ={
            title: this.state.title,
            status: false
        }
        this.setState((prevState) => ({ todolist: [...prevState.todolist, todolist ] , title: ''}))

    }
    render() {
        return (
            <div>
                <div className="search-bar">
                <input type="text" placeholder="Add Item here..." value={this.state.title} onChange={this.setData}/>
                    <input type="button" className="savebutton" value="save" onClick={this.saveData} />
                </div>
                <div className="card">
                      <AppTravel todolist={this.state.todolist} onDelete={this.onDelete} updatePending={this.updatePending}/>
                </div>
               
              
            </div>

        )
    }
}

export default TravelList
